mapper.select:
SELECT boardNum, userName, boardTitle, boardCategory, boardDate, boardReadCount,
FROM board b, user u
ORDER BY boardNum DESC LIMIT #{startRow} ,#{pageScale}

choose.when:
WHERE b.boardUserId = u.userId
AND boardUserId = #{searchUserId}
AND boardDate LIKE CONCAT( #{dateKeyword},'%')

choose.otherwise:
WHERE b.boardUserId = u.userId
AND boardUserId = #{searchUserId}
AND ${searchOption} LIKE CONCAT('%' ,#{keyword},'%')
